function 假人事件类:更新()
	print("正在更新------------看到我就说明出了问题")
	for i=1,#self.待更新事件 do
		if self.待更新事件[i].时间 == 0 then
			if self.待更新事件[i].事件 == "摆摊" then
				self:摆摊(self.待更新事件[i].数字id)
				table.remove(self.待更新事件,i)
				break
			end
		else
			self.待更新事件[i].时间 =self.待更新事件[i].时间 -1
		end
	end
end

function 假人事件类:循环移动事件(数字id)---真id
		local sjsz=取随机数(1,100)
		if sjsz > 80 then
			return
		elseif sjsz <= 80 and sjsz > 20 then
			self:挂机(数字id)
		elseif sjsz <= 20 and sjsz > 10 then
			self:抓鬼(数字id)
		elseif sjsz <= 10 then
			self:打怪(数字id)
		end

end

