function 假人事件类:摆摊执行中(数字id)----只执行一次了
	if 玩家数据[数字id].摊位数据==nil then
 	local sjs = self.摆摊地图[取随机数(1,#self.摆摊地图)]--取到摆摊的地图
	if self.摆摊数据[sjs][1] == nil then
		return
	end
	local sjs2 = 取随机数(1,#self.摆摊数据[sjs])---取到摆摊的随机坐标
	local sjs3 = {地图=sjs,x=self.摆摊数据[sjs][sjs2].x,y=self.摆摊数据[sjs][sjs2].y}--取到摆摊地图+坐标打包在一起
	for i=1,#self.飞行传送点 do
		if self.飞行传送点[i][1] == sjs3.地图 then----如果飞行传送点表中的表第1个值=摆摊地图号
			地图处理类:跳转地图(数字id,self.飞行传送点[i][1],self.飞行传送点[i][2],self.飞行传送点[i][3])
		end
	end
	地图处理类:移动请求("假人",sjs3,数字id)
	玩家数据[数字id].角色.地图数据.x,玩家数据[数字id].角色.地图数据.y = sjs3.x*20,sjs3.y*20
	table.remove(self.摆摊数据[sjs],sjs2)
	-- self.待更新事件[#self.待更新事件+1] = {时间=10,事件 = "摆摊",数字id = 数字id}
	self:摆摊(数字id)
	end
end

