function 假人事件类:取随机中文姓名()
  local 名称 = ''
  local 姓 = 姓库[取随机数(1,#姓库)]
  if math.random(100) <= 55 then
      名称 = 姓 .. 双字[取随机数(1,#双字)]
  elseif math.random(100) <= 70 then
      名称 = 姓 .. 单字[取随机数(1,#单字)]
  else
      名称 = 姓 .. 单字[取随机数(1,#单字)] .. 单字[取随机数(1,#单字)]
  end
  return 名称
end

